import { ChatContainer, MainContainer, Message, MessageInput, MessageList, TypingIndicator } from '@chatscope/chat-ui-kit-react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import React, { useState } from 'react';
import './App.css';

function App() {
  const [chatStarted, setChatStarted] = useState(false);
  const [typing, setTyping] = useState(false);
  const [messages, setMessages] = useState([
    {
      message: "Hello, I am ChatGPT",
      sender: "CHATGPT",
      direction: "incoming"
    }
  ]);

  const handleSend = async (message) => {
    const newMessage = {
      message: message,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newMessage];
    setMessages(newMessages);
    setTyping(true);
    await processMessageToChatGPT(newMessages);
  };

  async function processMessageToChatGPT(chatMessages) {
    const apiMessage = chatMessages.map((messageObject) => ({
      role: messageObject.sender === "ChatGPT" ? "assistant" : "user",
      content: messageObject.message
    }));

    const systemMessage = {
      role: "system",
      content: "Explain all concepts like I am 10 years old."
    };

    const apiRequestBody = {
      model: "gpt-3.5-turbo",
      messages: [systemMessage, ...apiMessage]
    };

    try {
      const response = await fetch("https://api.openai.com/v1/chat/completions", {
        method: "POST",
        headers: {
          "Authorization": "Bearer your_api_key",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(apiRequestBody)
      });

      if (!response.ok) {
        throw new Error('HTTP error', response.status);
      }

      const data = await response.json();
      setMessages(prevMessages => [
        ...prevMessages,
        {
          message: data.choices[0].message.content,
          sender: "ChatGPT"
        }
      ]);
    } catch (error) {
      console.error("Failed to fetch or parse:", error);
    } finally {
      setTyping(false);
    }
  }

  return (
    <div className="App" style={{ height: "100vh", display: "flex", flexDirection: "column" }}>
      <MainContainer style={{ flexGrow: 1, display: 'flex', flexDirection: 'column', height: '100%' }}>
        <ChatContainer style={{ flexGrow: 1 }}>
          <MessageList scrollBehavior="smooth" typingIndicator={typing ? <TypingIndicator content="ChatGPT is Typing" /> : null}>
            {messages.map((message, i) => (
              <Message key={i} model={{ message: message.message, direction: message.direction }}
                className={message.direction === "incoming" ? "incoming-message" : "outgoing-message"} />
            ))}
          </MessageList>
          {chatStarted && (
            <MessageInput placeholder="Type message here" onSend={handleSend} />
          )}
        </ChatContainer>
        {!chatStarted && (
          <button onClick={() => setChatStarted(true)} style={{ padding: '10px 20px', width: '100%', marginTop: '10px' }}>
            Start Chat
          </button>
        )}
      </MainContainer>
    </div>
  );
}

export default App;
