import { ChatContainer, MainContainer, Message, MessageInput, MessageList, TypingIndicator } from '@chatscope/chat-ui-kit-react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import React, { useState } from 'react';
import './App.css';


const API_KEY = "sk-None-D5lH5Bi5CZOzitfWuXiJT3BlbkFJKfXbg2qxC6EAVpiuSiDb";
function App() {
  const [typing, setTyping] = useState(false);
  const [messages, setMessages] = useState([
    {
      message: "Hello, I am ChatGPT",
      sender: "CHATGPT",
      direction: "incoming"
    }
  ]);
  const [chatStarted, setChatStarted] = useState(false);

  const handleSend = async (message) => {
    const newMessage = {
      message: message,
      sender: "user",
      direction: "outgoing"
    };

    const newMessages = [...messages, newMessage];
    setMessages(newMessages);
    setTyping(true);
    await processMessageToChatGPT(newMessages);
  };

  async function processMessageToChatGPT(chatMessages) {
    const apiMessage = chatMessages.map((messageObject) => ({
      role: messageObject.sender === "ChatGPT" ? "assistant" : "user",
      content: messageObject.message
    }));

    const systemMessage = {
      role: "system",
      content: "Explain all concepts like I am 10 years old."
    };

    const apiRequestBody = {
      model: "gpt-3.5-turbo",
      messages: [systemMessage, ...apiMessage]
    };

    const response = await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Authorization": "Bearer " + API_KEY,
        "Content-Type": "application/json"
      },
      body: JSON.stringify(apiRequestBody)
    });

    const data = await response.json();
    console.log(data);
    setMessages(prevMessages => [
      ...prevMessages,
      {
        message: data.choices[0].message.content,
        sender: "ChatGPT"
      }
    ]);
    setTyping(false);
  }

  return (
    <div className="App">
      <div style={{ position: "center", height: "600px", width: "600px" }}>
        <MainContainer>
          <ChatContainer>
            <MessageList
              scrollBehavior="smooth"
              typingIndicator={typing ? <TypingIndicator content="ChatGPT is Typing" /> : null}
            >
              {messages.map((message, i) => (
                <Message key={i} model={{
                  message: message.message,
                  direction: message.direction
                }}
                className={message.direction === "incoming" ? "incoming-message" : "outgoing-message"} />
              ))}
            </MessageList>
            {chatStarted ? (
              <MessageInput placeholder="Type message here" onSend={handleSend} />
            ) : (
              <button onClick={() => setChatStarted(true)} style={{ margin: '20px', padding: '10px 20px' }}>
                Start Chat
              </button>
            )}
          </ChatContainer>
        </MainContainer>
      </div>
    </div>
  );
}

export default App;
