import { ChatContainer, MainContainer, Message, MessageInput, MessageList, TypingIndicator } from '@chatscope/chat-ui-kit-react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import React, { useState } from 'react';
import './App.css';

const API_KEY = "sk-None-D5lH5Bi5CZOzitfWuXiJT3BlbkFJKfXbg2qxC6EAVpiuSiDb";

function App() {
  const[typing,setTyping]=useState(false);
  const[messages,setMessages]=useState([
    {
      message: "Hello,Iam ChatGPT",
      sender : "CHATGPT",
      direction :"incoming"

    }
  ]);


  const handleSend=async(message) =>{
    const newMessage={
      message : message,
      sender : "user",
      direction:"outgoing"
    }

    const newMessages=[...messages,newMessage];
    setMessages(newMessages);

    setTyping(true);

    await processMessageToChatGPT(newMessages);

  }

  async function processMessageToChatGPT(chatMessages)
  {
    let apiMessage=chatMessages.map((messageObject) =>
    {
      let role="";
      if(messageObject.sender === "ChatGPT")
      {
        role="assistant"
      }else
      {
        role="user"
      }

      return{role : role,content : messageObject.message}
    });

    const systemMessage={
      role : "system",
      content : "Explain all concepts like Iam 10 years old."
    }
    const apiRequestBody ={

      "model":"gpt-3.5-turbo",
      "messages":[
        systemMessage,
        ...apiMessage
      ]

    }

    await fetch("https://api.openai.com/v1/chat/completions",{

      method:"POST",
      headers:{
        "Authorization": "Bearer " + API_KEY,
        "Content-Type" : "application/json"
      },

      body : JSON.stringify(apiRequestBody)
    }).then((data) => {
      return data.json();
    }).then((data) =>{
      console.log(data);
      console.log(data.choices[0].message.content);
      setMessages(
        [
          ...chatMessages,{
            message : data.choices[0].message.content,
            sender : "ChatGPT"
          }
        ]
      );
      setTyping(false);
    });
  }
  return (
    <div className="App">
      <div style={{position: "relative",height:"800px",width:"700px"}}>
        <MainContainer>
          <ChatContainer>
          <MessageList 
          scrollBehavior='smooth'
          typingIndicator={typing? <TypingIndicator content="ChatGPT is Typing"/> : null}>

          {
            messages.map(
              (message,i)=>(
                <Message key={i} model={{ 
                  message: message.message, 
                  direction: message.direction 
                }} 
                className={message.direction === "incoming" ? "incoming-message" : "outgoing-message"}/>
              )
            )
          }
          </MessageList>

          <MessageInput placeholder="Type message here" onSend={(message) => handleSend(message)} />
          </ChatContainer>
        </MainContainer>
      </div>
    </div>
  );
}

export default App;
